mapper.select:
SELECT series || invoice_number::text AS fullInvoiceNumber,
        invoice.*
FROM invoice.invoice


        AND EXTRACT(YEAR FROM created_at) = #{req.year}


        AND created_at >= #{req.fromDate}


        AND created_at <= #{req.toDate}


        AND status = #{req.status}


        AND series = #{series}


        AND invoice_number = #{invoiceNumber}


        AND type = #{req.type}

    AND sender_id = #{id} AND status != 'DRAFT'

SELECT series || invoice_number::text AS fullInvoiceNumber,
invoice.*
FROM invoice.invoice


        AND EXTRACT(YEAR FROM created_at) = #{req.year}


        AND created_at >= #{req.fromDate}


        AND created_at <= #{req.toDate}


        AND status = #{req.status}


        AND series = #{series}


        AND invoice_number = #{invoiceNumber}


        AND type = #{req.type}

    AND customer_id = #{id} AND status != 'DRAFT'
